mapper.select:
SELECT SLCT.ESTM_NO ,SLCT.ESTM_OBJ_SEQ ,SLCT.ESTM_PROCD_SEQ ,SLCT.VEND_REG_NO ,SLCT.BIZRNO ,SLCT.VEND_NM ,SLCT.ESTM_CMTM_NO ,SLCT.ESTM_CMTM_NM ,( CASE WHEN ESTM_FRM_PROCD_SECD = 'C' AND SCR.ESTM_TOT_SCR = 100 THEN '적격' WHEN ESTM_FRM_PROCD_SECD = 'C' AND SCR.ESTM_TOT_SCR != 100 AND VAL_CNT > 0 THEN '부적격' WHEN ESTM_FRM_PROCD_SECD = 'C' AND VAL_CNT = 0 THEN '미평가' ELSE TO_CHAR(NVL(SCR.ESTM_TOT_SCR,0)) END ) AS CMTM_TOT_VAL FROM ( SELECT B.ESTM_NO ,B.ESTM_OBJ_SEQ ,B.ESTM_PROCD_SEQ ,B.VEND_REG_NO ,B.BIZRNO ,B.VEND_NM
, (SELECT ESTM_CHRGR_ID FROM T_ESTM_MNG_MST WHERE 1=1 AND ESTM_NO =B.ESTM_NO AND DEL_AT='N') AS ESTM_CMTM_NO , (SELECT ESTM_CHRGR_NM FROM T_ESTM_MNG_MST WHERE 1=1 AND ESTM_NO =B.ESTM_NO AND DEL_AT='N') AS ESTM_CMTM_NM ,(SELECT COUNT(1) FROM T_ESTM_OBJ_SCR WHERE ESTM_NO = B.ESTM_NO AND ESTM_OBJ_SEQ = B.ESTM_OBJ_SEQ AND ESTM_PROCD_SEQ = #{P_ESTM_PROCD_SEQ, jdbcType=VARCHAR} AND LENGTH(ESTM_ITEM_NO) = 2) AS VAL_CNT ,(SELECT ESTM_FRM_PROCD_SECD FROM T_ESTM_PROCD WHERE ESTM_NO = #{P_ESTM_NO, jdbcType=VARCHAR} AND ESTM_PROCD_SEQ = #{P_ESTM_PROCD_SEQ, jdbcType=VARCHAR}) AS ESTM_FRM_PROCD_SECD
FROM T_ESTM_OBJ_SLCT A ,(SELECT ESTM_NO , ESTM_OBJ_SEQ , #{P_ESTM_PROCD_SEQ, jdbcType=VARCHAR} AS ESTM_PROCD_SEQ , VEND_REG_NO , BIZRNO , VEND_NM FROM T_ESTM_OBJ WHERE 1=1 AND DEL_AT = 'N' ) B WHERE 1=1 AND B.ESTM_NO = A.ESTM_NO(+) AND B.ESTM_OBJ_SEQ = A.ESTM_OBJ_SEQ(+) AND B.ESTM_PROCD_SEQ = A.ESTM_PROCD_SEQ(+) AND B.ESTM_PROCD_SEQ = #{P_ESTM_PROCD_SEQ, jdbcType=VARCHAR} ) SLCT , T_ESTM_CMTM_OBJ_SCR SCR WHERE 1=1 AND SLCT.ESTM_NO = SCR.ESTM_NO(+) AND SLCT.ESTM_OBJ_SEQ = SCR.ESTM_OBJ_SEQ(+) AND SLCT.ESTM_PROCD_SEQ = SCR.ESTM_PROCD_SEQ(+) AND SLCT.ESTM_NO = #{P_ESTM_NO, jdbcType=VARCHAR} AND SLCT.ESTM_PROCD_SEQ = #{P_ESTM_PROCD_SEQ, jdbcType=VARCHAR}

SELECT ESTM_NO ,ESTM_CMTM_NO ,VEND_REG_NO ,VEND_NM ,BIZRNO ,ESTM_OBJ_SEQ ,( CASE WHEN ESTM_FRM_PROCD_SECD = 'C' AND CMTM_TOT_VAL = 100 THEN '적격' WHEN ESTM_FRM_PROCD_SECD = 'C' AND CMTM_TOT_VAL != 100 AND VAL_CNT > 0 THEN '부적격' WHEN ESTM_FRM_PROCD_SECD = 'C' AND VAL_CNT = 0 THEN '미평가' ELSE TO_CHAR(CMTM_TOT_VAL) END ) AS CMTM_TOT_VAL
FROM ( SELECT CMTM.ESTM_NO ,CMTM.ESTM_CMTM_NO ,CMTM.ESTM_CMTM_NM ,OBJ.VEND_REG_NO ,OBJ.VEND_NM ,OBJ.BIZRNO ,OBJ.ESTM_OBJ_SEQ ,NVL( ( SELECT SUM(ESTM_SCR) FROM T_ESTM_OBJ_SCR WHERE ESTM_NO = OBJ.ESTM_NO AND ESTM_OBJ_SEQ = OBJ.ESTM_OBJ_SEQ AND ESTM_PROCD_SEQ = #{P_ESTM_PROCD_SEQ, jdbcType=VARCHAR} AND ESTM_CMTM_NO = CMTM.ESTM_CMTM_NO AND LENGTH(ESTM_ITEM_NO) = 2
) , 0) AS CMTM_TOT_VAL ,(SELECT ESTM_FRM_PROCD_SECD FROM T_ESTM_PROCD WHERE ESTM_NO = #{P_ESTM_NO, jdbcType=VARCHAR} AND ESTM_PROCD_SEQ = #{P_ESTM_PROCD_SEQ, jdbcType=VARCHAR}) AS ESTM_FRM_PROCD_SECD ,(SELECT COUNT(1) FROM T_ESTM_OBJ_SCR WHERE ESTM_NO = OBJ.ESTM_NO AND ESTM_OBJ_SEQ = OBJ.ESTM_OBJ_SEQ AND ESTM_PROCD_SEQ = #{P_ESTM_PROCD_SEQ, jdbcType=VARCHAR} AND ESTM_CMTM_NO = CMTM.ESTM_CMTM_NO AND LENGTH(ESTM_ITEM_NO) = 2) AS VAL_CNT FROM T_ESTM_CMTM CMTM ,T_ESTM_OBJ OBJ WHERE 1=1 AND CMTM.ESTM_NO = OBJ.ESTM_NO AND CMTM.ESTM_NO = #{P_ESTM_NO} AND CMTM.SLCT_YN = 'Y' AND OBJ.DEL_AT = 'N' )

SELECT COUNT(1) FROM ( 
	
	
) T1
WHERE 1 = 1

        
        SELECT * FROM (
		    
			
			
		) T1
		WHERE 1 = 1

SELECT ESTM_NO , ESTM_CMTM_NO , ESTM_CMTM_SLCT_NGR , PRIO_RNK , SLCT_YN , ESTM_CMTM_NM , INO_CMTM_SECD , INO_CMTM_SENM , F_CDVAL_INQIRE('INO_CMTM_SECD', INO_CMTM_SECD) AS INO_CMTM_SECD_NM , ESTM_SPHE_SECD , ESTM_SPHE_SENM , ( SELECT ESTM_SPHE_SENM FROM T_ESTM_SPHE_MST A WHERE CM.ESTM_SPHE_SECD = A.ESTM_SPHE_SECD ) AS ESTM_SPHE_SECD_NM , LLF_SECD , LLF_NM , F_CDVAL_INQIRE('LLF_SECD', LLF_SECD) AS LLF_SECD_NM , CNTN_SECD , CNTN_NM , F_CDVAL_INQIRE('CNTN_SECD', CNTN_SECD) AS CNTN_SECD_NM , MLF_SECD , MLF_NM , F_CDVAL_INQIRE('MLF_SECD', MLF_SECD) AS MLF_SECD_NM , SLF_SECD , SLF_NM , F_CDVAL_INQIRE('SLF_SECD', SLF_SECD) AS SLF_SECD_NM , CP_NO , EMAL , LINE_TYP_SECD , LINE_TYP_SENM , F_CDVAL_INQIRE('LINE_TYP_SECD', LINE_TYP_SECD) AS LINE_TYP_SECD_NM , SLCT_SECD , SLCT_SENM , F_CDVAL_INQIRE('SLCT_SECD', SLCT_SECD) AS SLCT_SECD_NM , RMK , NVL((SELECT COUNT(*) FROM T_ESTM_CMTM T1 WHERE T1.SLCT_YN = 'Y' AND T1.INO_CMTM_SECD = 'OUT' AND ESTM_NO = #{P_ESTM_NO, jdbcType=VARCHAR}), 0) AS OUT_SLCT_CNT , NVL((SELECT COUNT(*) FROM T_ESTM_CMTM T1 WHERE T1.SLCT_YN = 'Y' AND T1.INO_CMTM_SECD = 'INN' AND ESTM_NO = #{P_ESTM_NO, jdbcType=VARCHAR}), 0) AS INN_SLCT_CNT
FROM T_ESTM_CMTM CM WHERE 1=1 AND ESTM_NO = #{P_ESTM_NO, jdbcType=VARCHAR}
AND CM.INO_CMTM_SECD = #{P_INO_CMTM_SECD, jdbcType=VARCHAR}
AND CM.ESTM_CMTM_SLCT_NGR = #{P_ESTM_CMTM_SLCT_NGR, jdbcType=VARCHAR}
ORDER BY TO_NUMBER(PRIO_RNK)

