mapper.insert:
INSERT INTO T_ESTM_CMTM ( ESTM_NO , ESTM_CMTM_NO , ESTM_CMTM_SLCT_NGR , PRIO_RNK , SLCT_YN , ESTM_CMTM_NM , INO_CMTM_SECD , INO_CMTM_SENM , ESTM_SPHE_SECD , ESTM_SPHE_SENM , LLF_SECD , LLF_NM , CNTN_SECD , CNTN_NM , MLF_SECD , MLF_NM , SLF_SECD , SLF_NM , CP_NO , EMAL , LINE_TYP_SECD , LINE_TYP_SENM , SLCT_SECD , SLCT_SENM , RMK
) SELECT #{P_ESTM_NO, jdbcType=VARCHAR} AS ESTM_NO , ESTM_CMTM_NO , TO_NUMBER(#{P_ESTM_CMTM_SLCT_NGR, jdbcType=INTEGER}) AS ESTM_CMTM_SLCT_NGR , TO_NUMBER('') AS PRIO_RNK , '' AS SLCT_YN , ESTM_CMTM_NM , INO_CMTM_SECD , INO_CMTM_SECD_NM AS INO_CMTM_SENM , ESTM_SPHE_SECD , ESTM_SPHE_SENM , LLF_SECD , LLF_NM , CNTN_SECD , CNTN_NM , MLF_SECD , MLF_NM , SLF_SECD , SLF_NM , CP_NO , EMAL , '' AS LINE_TYP_SECD , '' AS LINE_TYP_SENM , #{P_SLCT_SECD, jdbcType=INTEGER} AS SLCT_SECD , '' AS SLCT_SENM , '' AS RMK
FROM ( SELECT ROWNUM AS RNUM, T1.* FROM ( SELECT SM.ESTM_CMTM_NO , SM.ESTM_SPHE_SECD , SM.ESTM_SPHE_SENM , SM.ESTM_CMTM_NM , PM.INO_CMTM_SECD , F_CDVAL_INQIRE('INO_CMTM_SECD', PM.INO_CMTM_SECD) AS INO_CMTM_SECD_NM , PM.LLF_SECD , PM.LLF_NM , F_CDVAL_INQIRE('LLF_SECD', PM.LLF_SECD) AS LLF_SECD_NM , PM.CNTN_SECD , PM.CNTN_NM , F_CDVAL_INQIRE('CNTN_SECD', PM.CNTN_SECD) AS CNTN_SECD_NM , PM.MLF_SECD , PM.MLF_NM , F_CDVAL_INQIRE('MLF_SECD', PM.MLF_SECD) AS MLF_SECD_NM , PM.SLF_SECD , PM.SLF_NM , F_CDVAL_INQIRE('SLF_SECD', PM.SLF_SECD) AS SLF_SECD_NM , PM.CP_NO , PM.EMAL FROM T_ESTM_CMTM_SPHE_MPG SM , T_ESTM_CMTM_POOL_MST PM WHERE 1=1 AND SM.DEL_AT = 'N' AND PM.DEL_AT = 'N' AND SM.ESTM_CMTM_NO = PM.ESTM_CMTM_NO AND PM.INO_CMTM_SECD = #{P_INO_CMTM_SECD, jdbcType=VARCHAR} AND SM.ESTM_SPHE_SECD = #{P_ESTM_SPHE_SECD, jdbcType=VARCHAR} ORDER BY DBMS_RANDOM.VALUE ) T1 ) WHERE ROWNUM <= #{P_SLCT_CNT, jdbcType=INTEGER}

