select ESTM_NO ,ESTM_PROCD_SEQ ,(SELECT ESTM_PROCD_NM FROM T_ESTM_PROCD WHERE 1=1 AND ESTM_NO = A.ESTM_NO AND ESTM_PROCD_SEQ = A.ESTM_PROCD_SEQ) AS ESTM_PROCD_NM ,(SELECT ESTM_PROCD_SECD FROM T_ESTM_PROCD WHERE 1=1 AND ESTM_NO = A.ESTM_NO AND ESTM_PROCD_SEQ = A.ESTM_PROCD_SEQ) AS ESTM_PROCD_SECD ,(SELECT ESTR_SECD FROM T_ESTM_PROCD WHERE 1=1 AND ESTM_NO = A.ESTM_NO AND ESTM_PROCD_SEQ = A.ESTM_PROCD_SEQ) AS ESTR_SECD ,(SELECT ESTM_FRM_PROCD_SECD FROM T_ESTM_PROCD WHERE 1=1 AND ESTM_NO = A.ESTM_NO AND ESTM_PROCD_SEQ = A.ESTM_PROCD_SEQ) AS ESTM_FRM_PROCD_SECD from ( select ESTM_NO ,ESTM_OBJ_SEQ ,ESTM_PROCD_SEQ ,ESTM_ITEM_NO ,ESTM_CMTM_NO ,ESTM_SCR ,ESTM_PROCD_SEQ || '-' || ESTM_ITEM_NO AS ESTM_NUMBER from T_ESTM_OBJ_SCR where 1=1 and estm_no = #{P_ESTM_NO, jdbcType=VARCHAR} and length(estm_item_no) = 2
and ESTM_PROCD_SEQ || '-' || ESTM_ITEM_NO IN
#{SEARCH_ITEM, jdbcType=VARCHAR}
and 1!=1
UNION ALL SELECT A.ESTM_NO ,A.ESTM_OBJ_SEQ ,A.ESTM_PROCD_SEQ ,'' AS ESTM_ITEM_NO ,A.ESTM_CMTM_NO ,A.ESTM_TOT_SCR AS ESTM_SCR , 'CMTM-' || A.ESTM_PROCD_SEQ AS ESTM_NUMBER FROM T_ESTM_CMTM_OBJ_SCR A WHERE 1=1 AND A.ESTM_NO = #{P_ESTM_NO, jdbcType=VARCHAR}
AND 'CMTM-'||A.ESTM_PROCD_SEQ IN
#{SEARCH_ITEM, jdbcType=VARCHAR}
and 1!=1
GROUP BY A.ESTM_NO,A.ESTM_OBJ_SEQ,A.ESTM_PROCD_SEQ,A.ESTM_CMTM_NO,A.ESTM_TOT_SCR UNION ALL SELECT A.ESTM_NO ,A.ESTM_OBJ_SEQ ,A.ESTM_PROCD_SEQ ,'' AS ESTM_ITEM_NO ,'' AS ESTM_CMTM_NO ,A.ESTM_TOT_SCR AS ESTM_SCR , 'TOT-' || A.ESTM_PROCD_SEQ AS ESTM_NUMBER FROM T_ESTM_OBJ_SLCT A WHERE 1=1 AND A.ESTM_NO = #{P_ESTM_NO, jdbcType=VARCHAR}
AND 'TOT-'||A.ESTM_PROCD_SEQ IN
#{SEARCH_ITEM, jdbcType=VARCHAR}
and 1!=1
GROUP BY A.ESTM_NO,A.ESTM_OBJ_SEQ,A.ESTM_PROCD_SEQ,A.ESTM_TOT_SCR ) A GROUP BY A.ESTM_NO ,A.ESTM_PROCD_SEQ ORDER BY A.ESTM_PROCD_SEQ

select A.ESTM_NO ,A.ESTM_PROCD_SEQ ,A.ESTM_ITEM_NO ,A.ESTM_ITEM_NM ,A.ESTM_NUMBER from ( SELECT A.ESTM_NO ,A.ESTM_PROCD_SEQ ,A.ESTM_ITEM_NO ,A.ESTM_ITEM_NM ,A.ESTM_PROCD_SEQ || '-' || A.ESTM_ITEM_NO AS ESTM_NUMBER FROM T_ESTM_PROCD_FRM A
WHERE 1=1 AND A.ESTM_NO = #{P_ESTM_NO, jdbcType=VARCHAR} AND length(A.ESTM_ITEM_NO) = 2
AND ESTM_PROCD_SEQ || '-' || ESTM_ITEM_NO IN
#{SEARCH_ITEM, jdbcType=VARCHAR}
AND 1!=1
UNION SELECT A.ESTM_NO ,A.ESTM_PROCD_SEQ ,'999' AS ESTM_ITEM_NO ,DECODE((SELECT ESTR_SECD FROM T_ESTM_PROCD WHERE 1=1 AND ESTM_NO = A.ESTM_NO AND ESTM_PROCD_SEQ = A.ESTM_PROCD_SEQ),'A','평가위원총점','B','평가담당자총점') AS ESTM_ITEM_NM , 'CMTM-' || A.ESTM_PROCD_SEQ AS ESTM_NUMBER FROM T_ESTM_CMTM_OBJ_SCR A WHERE 1=1 AND A.ESTM_NO = #{P_ESTM_NO, jdbcType=VARCHAR}
AND 'CMTM-'||A.ESTM_PROCD_SEQ IN
#{SEARCH_ITEM, jdbcType=VARCHAR}
AND 1!=1
UNION SELECT A.ESTM_NO ,A.ESTM_PROCD_SEQ ,'9999' AS ESTM_ITEM_NO ,'평가총점' AS ESTM_ITEM_NM , 'TOT-' || A.ESTM_PROCD_SEQ AS ESTM_NUMBER FROM T_ESTM_OBJ_SLCT A WHERE 1=1 AND A.ESTM_NO = #{P_ESTM_NO, jdbcType=VARCHAR}
AND 'TOT-'||A.ESTM_PROCD_SEQ IN
#{SEARCH_ITEM, jdbcType=VARCHAR}
and 1!=1
) A ORDER BY ESTM_PROCD_SEQ ASC, TO_NUMBER(ESTM_ITEM_NO) ASC
